import React, { useState, useRef } from 'react';
import { Box, Typography } from '@mui/material';

const OptionTab = () => {
  const [list, setList] = useState(['Item 1', 'Item 2', 'Item 3']);
  const dragItem = useRef(null);

  const dragStartHandler = (e, position) => {
    dragItem.current = position;
    e.dataTransfer.setData('text/plain', position.toString());
  };

  const dragOverHandler = (e) => {
    e.preventDefault();
  };

  const dropHandler = (e) => {
    e.preventDefault();
    const newPosition = list.length;

    const itemIndex = parseInt(e.dataTransfer.getData('text/plain'));

    const copyListItems = [...list];
    const [draggedItem] = copyListItems.splice(itemIndex, 1);
    copyListItems.splice(newPosition, 0, draggedItem);
    setList(copyListItems);
    dragItem.current = null;
  };

  return (
    <div
      style={{
        display: 'flex',
        width: '100%',
        overflowX: 'auto',
        whiteSpace: 'nowrap',
      }}
      onDragOver={dragOverHandler}
      onDrop={dropHandler}
    >
      {list.map((item, index) => (
        <Box
          key={index}
          sx={{
            minWidth: '150px',
            border: '1px solid #ccc',
            padding: '10px',
            marginRight: '10px',
            display: 'inline-block',
            verticalAlign: 'top',
          }}
          onDragStart={(e) => dragStartHandler(e, index)}
          draggable
        >
          <Typography variant="h6">{item}</Typography>
        </Box>
      ))}
    </div>
  );
};

export default OptionTab;
