import React, { useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider, Paper } from '@mui/material';
import { useParams } from 'react-router-dom';
import Draggable from 'react-draggable';

const PreDefinedPath = ({ pathways }) => {
  const { id } = useParams();
  const initialSteps = pathways[id]?.steps || [];
  const [reorderedSteps, setReorderedSteps] = useState(initialSteps);
  const [positions, setPositions] = useState(initialSteps.map(() => ({ x: 0, y: 0 })));

  const onDragEnd = (result, stepIndex) => {
    if (!result || !result.x || !result.y) return;

    // Calculate the position of the dropped item
    const itemPositionX = result.x;
    const itemPositionY = result.y;

    // Calculate the target position (you can define your own logic here)
    const targetX = stepIndex * 200; // Example: X-coordinate where you want the item to snap
    const targetY = 0; // Example: Y-coordinate where you want the item to snap

    // Check if the item is close enough to the target position
    const isCloseEnough =
      Math.abs(itemPositionX - targetX) < 50 && Math.abs(itemPositionY - targetY) < 50;

    if (isCloseEnough) {
      // Snap the item to the target position
      const newPositions = [...positions];
      newPositions[stepIndex] = { x: targetX, y: targetY };
      setPositions(newPositions);
      setReorderedSteps((prevSteps) => {
        const updatedSteps = [...prevSteps];
        const [draggedItem] = updatedSteps.splice(stepIndex, 1);
        updatedSteps.splice(targetX / 200, 0, draggedItem);
        return updatedSteps;
      });
    }
  };

  if (!pathways[id]) {
    return <div>Pathway not found</div>;
  }

  return (
    <Container>
      <Typography variant="h4">{pathways[id].title} Pathway</Typography>
      <List>
        {reorderedSteps.map((step, index) => (
          <Draggable
            key={index}
            onStop={(event, data) => onDragEnd(data, index)}
            defaultPosition={positions[index]}
          >
            <div>
              <Paper elevation={3} style={{ padding: '8px', marginBottom: '8px' }}>
                <ListItem>
                  <ListItemText primary={`${index + 1}. ${step}`} />
                </ListItem>
              </Paper>
            </div>
          </Draggable>
        ))}
      </List>
    </Container>
  );
};

export default PreDefinedPath;
