import React, { useState } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from '../components/OptionTab';
import Sidebar from '../components/Sidebar';

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]);
  const [nextItemId, setNextItemId] = useState(1); // Unique identifier for items
  const [selectedCategory, setSelectedCategory] = useState(null); // Default selected category
  const [droppedItemTitles, setDroppedItemTitles] = useState([]); // Track dropped item titles

  // Update the data structure to match the new format
  const undefinedData = {
    games: [
      {
        id: 1,
        title: 'Introduction to Game Development',
        description: 'Learn the basics of game development.',
        skills: [
          {
            name: 'Programming',
            description: 'Basic programming concepts for games.',
            type: 'Fundamental',
          },
          // Add more skills as needed
        ],
      },
      // Add more game-related items here
    ],
    software_engineering: [
      {
        id: 1,
        title: 'Software Development',
        description: 'Software development involves the design, creation, and maintenance of software applications.',
        skills: [
          {
            name: 'Programming Languages',
            description: 'Proficiency in various programming languages such as Java, Python, C++, etc.',
            type: 'Skill',
          },
          // Add more skills as needed
        ],
      },
      // Add more software engineering-related items here
    ],
    // Add other technology categories here
  };

  // Handle category selection
  const handleCategorySelection = (category) => {
    setSelectedCategory(category);
  };

  // ... Rest of your component code ...

  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      <CssBaseline />
      <Sidebar data={undefinedData} onSelectCategory={handleCategorySelection} />
      {selectedCategory && (
        <div
          style={{
            display: 'flex',
            overflowX: 'auto',
            whiteSpace: 'nowrap',
            padding: '10px',
            border: '1px solid #ccc',
          }}
        >
          <OptionTab
            items={undefinedData[selectedCategory]}
            onDropInArea={handleDropInArea}
            droppedItemTitles={droppedItemTitles}
          />
        </div>
      )}
      {/* ... Rest of your component code ... */}
    </div>
  );
};

export default Undefined;
