import React, { useState } from 'react';
import { Typography } from '@mui/material';

const Sidebar = ({ data, onSelectCategory }) => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);

  const handleCategoryClick = (category, subcategory) => {
    setSelectedCategory(category);
    setSelectedSubcategory(subcategory);
    onSelectCategory({ category, subcategory }); // Call the callback function with the selected category and subcategory
  };

  return (
    <div style={{ width: '250px', position: 'fixed', top: '200px', left: 0, height: '100%', background: '#fff' }}>
      <h2>Categories</h2>
      <ul>
        {Object.keys(data).map((category) => (
          <li key={category}>
            <Typography
              variant="body1"
              style={{
                cursor: 'pointer',
                color: selectedCategory === category ? 'blue' : 'black',
                fontWeight: selectedCategory === category ? 'bold' : 'normal',
              }}
              onClick={() => handleCategoryClick(category, null)}
            >
              {category}
            </Typography>
            {selectedCategory === category && (
              <ul>
                {data[category].map((subcategory) => (
                  <li
                    key={subcategory}
                    style={{
                      cursor: 'pointer',
                      color: selectedSubcategory === subcategory ? 'blue' : 'black',
                    }}
                    onClick={() => handleCategoryClick(category, subcategory)}
                  >
                    {subcategory}
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
