import React, { useState } from 'react';
import Sidebar from '../components/Sidebar';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from '../components/OptionTab';

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]); // State to store dropped items

  // Handle the drop action
  const dropHandler = (e, item) => {
    e.preventDefault();
    // Add the dropped item to the list of dropped items
    setDroppedItems((prevItems) => [...prevItems, item]);
  };

  return (
    <div style={{ display: 'flex' }}>
      <CssBaseline />
      <Sidebar />
      <Container
        style={{
          minHeight: '100vh',
          border: '2px dashed #ccc', // Add a dashed border to indicate the droppable area
          padding: '20px',
          position: 'relative', // Ensure relative positioning for absolute children
        }}
      >
        {/* Your main content goes here */}
        <h1>Main Content</h1>

        {/* Render dropped items within the droppable area */}
        {droppedItems.map((item, index) => (
          <div
            key={index}
            style={{
              border: '1px solid #ccc',
              padding: '10px',
              marginBottom: '10px',
            }}
          >
            {item}
          </div>
        ))}

        {/* OptionTab component rendered within the droppable area */}
        <OptionTab onDrop={(item) => dropHandler(item)} />
      </Container>
    </div>
  );
};

export default Undefined;
