import React, { useState } from 'react';

const PathStepBox = ({ step }) => { // Receive a single step as a prop
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    if (currentStep < step.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };
console.log(step)
  // Get the current step using currentStep
console.log(step.details)
  return (
    <div>
     <h4>{step.content}</h4>
     {step.details.map((deets, index) => (
    <div key={index}>
        <p><strong>Name:</strong> {deets.title}</p>
        <p><strong>Description:</strong> {deets.content}</p>
        <ul className="skills-list">
            {deets.skills && deets.skills.map((skill, skillIndex) => (
                <li key={skillIndex} style={{ width: '90%' }}>
                      <p>{skill}</p>
                </li>
            ))}
        </ul>
    </div>
    ))}


      {step.resources && step.resources.length > 0 && (
        <div>
          <h3>Learning Resources:</h3>
          <ul>
            {step.resources.map((resource, index) => (
              <li key={index}>
                <a href={resource.link} target="_blank" rel="noopener noreferrer">
                  {resource.title}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}


    </div>
  );
};

export default PathStepBox;
