import React, { useState, useEffect } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from '../components/OptionTab';
import undefinedData from '../data/undefinedData.json';
import Sidebar from '../components/Sidebar';

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]);
  const [nextItemId, setNextItemId] = useState(1); // Unique identifier for items
  const [selectedCategory, setSelectedCategory] = useState('games');

  const handleDropInArea = (item) => {
    setDroppedItems([...droppedItems, { id: nextItemId, content: item }]);
    setNextItemId(nextItemId + 1);
  };

  const handleDropInContent = (draggedItemId, dropPosition) => {
    // ... (Your existing code for handling dropped items)
  };

  useEffect(() => {
    // Use useEffect to ensure that the selectedCategory is updated before rendering OptionTab
    console.log(selectedCategory); // Verify that the selectedCategory is updated
  }, [selectedCategory]);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category); // Update the selected category
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      <CssBaseline />

      <Sidebar data={undefinedData} onSelectCategory={handleCategoryChange} />
      <div
        style={{
          display: 'flex',
          overflowX: 'auto',
          whiteSpace: 'nowrap',
          padding: '10px',
          border: '1px solid #ccc',
        }}
      >
        <OptionTab items={undefinedData[selectedCategory]} onDropInArea={handleDropInArea} />
      </div>

      <Container
        style={{
          minHeight: '100vh',
          border: '2px dashed #ccc',
          padding: '20px',
          position: 'relative',
        }}
      >
        <h1>Main Content</h1>

        <div
          style={{
            width: '200px',
            height: '200px',
            border: '2px dashed #ccc',
            marginBottom: '20px',
          }}
          onDrop={(e) => {
            e.preventDefault();
            const item = e.dataTransfer.getData('text/plain');
            handleDropInArea(item);
          }}
          onDragOver={(e) => e.preventDefault()}
        >
          {droppedItems.map((item) => (
            <div
              key={item.id}
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginBottom: '10px',
                cursor: 'move',
              }}
              draggable
              onDragStart={(e) => {
                e.dataTransfer.setData('text/plain', item.id.toString());
              }}
              onDragEnd={() => handleDropInContent(item.id, -1)}
            >
              {item.content}
            </div>
          ))}
        </div>
      </Container>
    </div>
  );
};

export default Undefined;
