import React from 'react';
import { useParams } from 'react-router-dom';
import PathStepBox from '../components/PathStepBox' // Import the PathStepBox component

const PathInfoPage = ({ pathways }) => {
  const { id } = useParams();
  const path = pathways[id];

  if (!path) {
    return <div>Path not found</div>;
  }

  return (
    <div>
      <h1>{path.title}</h1>
      <p>{path.desc}</p>
      <p>Average Pay: ${path.pay}</p>

      <h2>Steps:</h2>
      {path.steps.map((step, index) => (
        <div key={index}>
          <PathStepBox step={step} /> {/* Pass the step data to PathStepBox */}
        </div>
      ))}

      {path.skills && path.skills.length > 0 && (
        <div>
          <h2>Skills You'll Gain:</h2>
          <ul>
            {path.skills.map((skill, skillIndex) => (
              <li key={skillIndex}>
                <strong>{skill.name}:</strong> {skill.description}{' '}
                <span style={{ color: 'red' }}>{skill.type} skill</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default PathInfoPage;
