import React, { useState } from 'react';
import { Box, Typography } from '@mui/material';

const OptionTab = ({ items }) => {
  const [list, setList] = useState(items);

  const dragStartHandler = (e, item) => {
    e.dataTransfer.setData('text/plain', item.title);
  };

  const dropHandler = (e, index) => {
    e.preventDefault();
    const draggedTitle = e.dataTransfer.getData('text/plain');
    const updatedList = [...list];
    const droppedItemIndex = updatedList.findIndex((item) => item.title === draggedTitle);

    if (droppedItemIndex !== -1) {
      updatedList.splice(droppedItemIndex, 1);
      updatedList.splice(index, 0, items[droppedItemIndex]);
      setList(updatedList);
    }
  };

  const dragOverHandler = (e) => {
    e.preventDefault();
  };

  return (
    <div
      style={{
        display: 'flex',
        width: '100%',
        overflowX: 'auto',
        whiteSpace: 'nowrap',
      }}
    >
      {list.map((item, index) => (
        <Box
          key={item.id}
          sx={{
            minWidth: '150px',
            border: '1px solid #ccc',
            padding: '10px',
            marginRight: '10px',
            display: 'inline-block',
            verticalAlign: 'top',
          }}
          onDragStart={(e) => dragStartHandler(e, item)}
          draggable
          onDrop={(e) => dropHandler(e, index)}
          onDragOver={dragOverHandler}
        >
          <Typography variant="h6">{item.title}</Typography>
        </Box>
      ))}
    </div>
  );
};

export default OptionTab;
