// Sidebar.js
import React, { useState } from 'react';

const Sidebar = ({ titles, onSelectTitle }) => {
  const [selectedTitle, setSelectedTitle] = useState(null);

  const handleTitleClick = (title) => {
    setSelectedTitle(title);
    onSelectTitle(title);
  };

  return (
    <div>
      <h2>Categories</h2>
      <ul>
        {titles.map((title) => (
          <li
            key={title}
            onClick={() => handleTitleClick(title)}
            style={{ cursor: 'pointer', color: selectedTitle === title ? 'blue' : 'black' }}
          >
            {title}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
