import React, { useState, useRef } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { Container, Typography, Button, Box, Accordion, AccordionSummary, AccordionDetails } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import theme from './Theme';
import { useNavigate } from 'react-router-dom';

const DefinedPath = () => {
  const { id } = useParams();
  const location = useLocation();
  const droppedItems = location.state ? location.state.droppedItems : [];
  const navigate = useNavigate();

  const [expandedItem, setExpandedItem] = useState(null);

  const toggleDetails = (itemIndex) => {
    if (expandedItem === itemIndex) {
      setExpandedItem(null);
    } else {
      setExpandedItem(itemIndex);
    }
  };

  const handleExplorePathwayClick = () => {
    navigate(`/pathinfo/${id}`, { state: { updatedList: droppedItems } });
  };

  const containerStyle = {
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  };

  const particleBackgroundStyle = {
    position: 'relative',
    padding: '20px',
    textAlign: 'center',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)',
    marginBottom: '20px',
    overflow: 'hidden',
    width: '80%',
  };

  const listItemStyle = {
    position: 'relative',
    boxShadow: '0px 0px 5px rgba(0, 0, 0, 0.3)',
    transition: 'background-color 0.3s',
    cursor: 'pointer',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '10px',
    textAlign: 'left',
    color: 'black',
    marginBottom: '20px',
    width: '100%',
  };

  return (
    <div style={containerStyle}>
      <div style={particleBackgroundStyle}>
        {/* Particle background */}
        <h1 style={{ fontSize: '2rem', marginBottom: '10px' }}>Undefined Path Title</h1>
        <p style={{ fontSize: '1.2rem', marginBottom: '10px' }}>Description of Undefined Path</p>
        <p style={{ fontSize: '1rem', color: theme.colors.text }}>Average Pay: $Undefined</p>
      </div>

      <div style={{ width: '80%' }}>
        {droppedItems.map((item, index) => (
          <Box
            sx={listItemStyle}
            key={index}
            onClick={() => toggleDetails(index)}
          >
            <h1 style={{ marginBottom: '10px' }}>{index + 1}</h1>
            {item.content}
            <Accordion expanded={expandedItem === index} style={{ width: '100%' }}>
              <AccordionSummary
                sx={{ backgroundColor: theme.colors.primary, color: theme.colors.text }}
                expandIcon={<ExpandMoreIcon />}
                onClick={(e) => {
                  e.stopPropagation();
                  toggleDetails(index);
                }}
              >
                <Typography variant="h6">Skills You'll Gain</Typography>
              </AccordionSummary>
              <AccordionDetails>
                <ul className="skills-list">
                  {item.skills && item.skills.map((skill, skillIndex) => (
                    <li key={skillIndex} style={{ width: '90%' }}>
                      <strong>{skill.name}</strong>: {skill.description} <span style={{ color: 'red' }}>{skill.type} skill</span>
                    </li>
                  ))}
                </ul>
              </AccordionDetails>
            </Accordion>
          </Box>
        ))}
      </div>

      <Button
        onClick={handleExplorePathwayClick}
        style={{ display: 'block', margin: '0 auto', fontSize: '20px', marginTop: '20px', zIndex: 1 }}
      >
        Explore Pathway
      </Button>
    </div>
  );
};

export default DefinedPath;
