import React, { useState } from 'react';
import { useParams, useLocation, Link } from 'react-router-dom';
import PathStepBox from '../components/PathStepBox';
import {
  Container,
  Typography,
  Paper,
  Button,
  Box,
} from '@mui/material';

const PathInfoPage = ({ pathways }) => {
  const { id } = useParams();
  const location = useLocation();
  const updatedList = location.state ? location.state.updatedList : null;
  const path = pathways[id];
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    if (currentStep < (updatedList || path.steps).length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  if (!path) {
    return (
      <Container maxWidth="lg">
      <Paper elevation={3} sx={{ padding: 3, marginTop: 2 }}>
        <Typography variant="h4" gutterBottom>
          {updatedList.title}
        </Typography>
        <Typography variant="body1" paragraph>
          {updatedList.content}
        </Typography>
        <Typography variant="body1">
          Average Pay: ${path.pay}
        </Typography>

        <Box marginTop={2}>
          <Typography variant="h5" gutterBottom>
            Step {currentStep + 1}
          </Typography>
          <PathStepBox step={currentStepContent} />
          <Box display="flex" justifyContent="space-between" marginTop={2}>
            <Button
              variant="contained"
              color="primary"
              onClick={prevStep}
              disabled={currentStep === 0}
            >
              Previous Step
            </Button>
            <Button
              variant="contained"
              color="primary"
              onClick={nextStep}
              disabled={currentStep === steps.length - 1}
            >
              Next Step
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
    )
  }

  const steps = updatedList || path.steps;
  const currentStepContent = steps[currentStep];
if(path){
  return (
    <Container maxWidth="lg">
      <Paper elevation={3} sx={{ padding: 3, marginTop: 2 }}>
        <Typography variant="h4" gutterBottom>
          {steps.title}
        </Typography>
        <Typography variant="body1" paragraph>
          {updatedList[0].title || path.desc}
        </Typography>
        <Typography variant="body1">
          Average Pay: ${path.pay}
        </Typography>

        <Box marginTop={2}>
          <Typography variant="h5" gutterBottom>
            Step {currentStep + 1}
          </Typography>
          <PathStepBox step={currentStepContent} />
          <Box display="flex" justifyContent="space-between" marginTop={2}>
            <Button
              variant="contained"
              color="primary"
              onClick={prevStep}
              disabled={currentStep === 0}
            >
              Previous Step
            </Button>
            <Button
              variant="contained"
              color="primary"
              onClick={nextStep}
              disabled={currentStep === steps.length - 1}
            >
              Next Step
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
}

};

export default PathInfoPage;
