const theme = {
  colors: {
    background: '#121212',     // Dark Gray (background)
    text: '#FFFFFF',           // White (text)
    primary: '#FF5722',        // Deep Orange (primary)
    secondary: '#2196F3',      // Blue (secondary)
    accent: '#FFC107',         // Amber (accent)
    highlight: '#4CAF50',      // Green (highlight)
  },
};



export default theme;
