import React, { useState } from 'react';
import { Accordion, AccordionSummary, AccordionDetails, Typography } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import jsPDF from 'jspdf';

const PathStepBox = ({ step }) => {
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    if (currentStep < (step.details || []).length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const generatePDF = () => {
    const doc = new jsPDF();
    let yOffset = 10; // Initial y position for content

    doc.text(String(step.content), 10, yOffset); // Title
    yOffset += 15; // Adjust y position

    const details = step.details || []; // Handle undefined or non-array details

    step.steps.forEach((s, stepIndex) => {
      s.details.forEach((deets) => {
        // Section Title
        doc.setFontSize(12);
        yOffset += 10;

        // Subsection Title
        doc.setFontSize(10);
        doc.text(`Name:`, 15, yOffset);
        const nameLines = doc.splitTextToSize(String(deets.title), 150);
        doc.text(nameLines, 25, yOffset);
        yOffset += (nameLines.length * 7) + 10; // Add extra space

        // Description
        doc.text(`Description:`, 15, yOffset);
        const descriptionLines = doc.splitTextToSize(String(deets.content), 150);
        doc.text(descriptionLines, 25, yOffset);
        yOffset += (descriptionLines.length * 7) + 15; // Add extra space

        deets.skills.forEach((skill) => {
          // Skill Title
          doc.text(`Skill:`, 20, yOffset);
          const skillLines = doc.splitTextToSize(String(skill.title), 145);
          doc.text(skillLines, 30, yOffset);
          yOffset += (skillLines.length * 7) + 10; // Add extra space

          // Resource
          if (skill.resource) {
            doc.text(`Resource:`, 25, yOffset);
            const resourceLines = doc.splitTextToSize(String(skill.resource), 140);
            yOffset += (resourceLines.length * 7) + 50; // Add extra space
            doc.text(resourceLines, 35, yOffset);

          }
        });

        yOffset += 10; // Adjust y position between sections
      });

      yOffset += 10; // Adjust y position between steps
    });

    doc.save('path.pdf');
  };



  return (
    <div>
      <h4>{step.content}</h4>
      <button onClick={generatePDF}>Download PDF</button>
      {step.details ||
        step.steps.map((item, index) =>
          item.details.map((deets, deetsIndex) => (
            <div key={deetsIndex}>
              <p>
                <strong>Name:</strong> {deets.title}
              </p>
              <p>
                <strong>Description:</strong> {deets.content}
              </p>
              <Accordion>
                <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                  <Typography variant="h6">Resources</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <ul className="skills-list">
                    {deets.skills &&
                      deets.skills.map((skill, skillIndex) => (
                        <li key={skillIndex}>
                          <strong>{skill.title}</strong>: {skill.resource && (
                            <a href={skill.resource} target="_blank" rel="noopener noreferrer">
                              {"Getting started with: " + skill.title}
                            </a>
                          )}
                        </li>
                      ))}
                  </ul>
                </AccordionDetails>
              </Accordion>
            </div>
          ))
        )}
    </div>
  );
};

export default PathStepBox;
