import React, { useState } from 'react';
import { Box, Typography } from '@mui/material';

const OptionTab = () => {
  const [list, setList] = useState(['Item 1', 'Item 2', 'Item 3']);

  const dragStartHandler = (e, item) => {
    e.dataTransfer.setData('text/plain', item); // Send the item content
  };

  const dragOverHandler = (e) => {
    e.preventDefault();
  };

  return (
    <div
      style={{
        display: 'flex',
        overflowX: 'auto',
        whiteSpace: 'nowrap',
        padding: '10px',
        border: '1px solid #ccc',
      }}
    >
      {list.map((item, index) => (
        <Box
          key={index}
          sx={{
            minWidth: '150px',
            border: '1px solid #ccc',
            padding: '10px',
            marginRight: '10px',
            display: 'inline-block',
            verticalAlign: 'top',
          }}
          onDragStart={(e) => dragStartHandler(e, item)} // Pass the item content
          draggable
        >
          <Typography variant="h6">{item}</Typography>
        </Box>
      ))}
    </div>
  );
};

export default OptionTab;
