import React, { useState } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from '../components/OptionTab';
import undefinedData from '../data/undefinedData.json';
import Sidebar from '../components/Sidebar';

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]);
  const [nextItemId, setNextItemId] = useState(1); // Unique identifier for items
  const [selectedCategory, setSelectedCategory] = useState('games'); // Default selected category

  const handleDropInArea = (item) => {
    const selectedItem = undefinedData[selectedCategory].find((dataItem) => dataItem.title === item);
    if (selectedItem) {
      setDroppedItems([...droppedItems, { id: nextItemId, ...selectedItem }]);
      setNextItemId(nextItemId + 1);
    }
  };

  const handleDropInContent = (draggedItemId, dropPosition) => {
    const updatedItems = [...droppedItems];
    const draggedItemIndex = updatedItems.findIndex((item) => item.id === draggedItemId);

    if (draggedItemIndex === -1) {
      return; // Handle cases where the item is not found
    }

    const [draggedItem] = updatedItems.splice(draggedItemIndex, 1);

    if (dropPosition === -1) {
      updatedItems.push(draggedItem); // Insert at the end
    } else {
      updatedItems.splice(dropPosition, 0, draggedItem); // Insert at the specified position
    }

    setDroppedItems(updatedItems);
  };

  const onDropHandler = (e) => {
    e.preventDefault();
    const item = e.dataTransfer.getData('text/plain');
    handleDropInArea(item);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      <CssBaseline />

      <Sidebar data={undefinedData} onSelectCategory={setSelectedCategory} />
      <div
        style={{
          display: 'flex',
          overflowX: 'auto',
          whiteSpace: 'nowrap',
          padding: '10px',
          border: '1px solid #ccc',
        }}
      >
        <OptionTab items={undefinedData[selectedCategory]} onDropInArea={handleDropInArea} />
      </div>

      <Container
        style={{
          height: '80%',
          border: '2px dashed #ccc',
          padding: '20px',
          position: 'relative',
        }}
      >
        <h1>Main Content</h1>

        <div
  style={{
    width: '100%',

    border: '2px dashed #ccc',

    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', // Adjust the column width as needed
  }}
  onDrop={onDropHandler}
  onDragOver={(e) => e.preventDefault()}
>
  {droppedItems.map((item) => (
    <div
      key={item.id}
      style={{
        border: '1px solid #ccc',
        padding: '10px',
        marginBottom: '10px',
        cursor: 'move',
        width: '100%', // Adjust the width as needed
        height: '200px', // Adjust the height as needed
      }}
      draggable
      onDragStart={(e) => {
        // Set JSON data of the item being dragged
        e.dataTransfer.setData('application/json', JSON.stringify(item));
      }}
      onDragEnd={() => handleDropInContent(item.id, -1)}
    >
      <h3>{item.title}</h3>
      <p>{item.description}</p>
    </div>
  ))}
</div>

      </Container>
    </div>
  );
};

export default Undefined;
