// Undefined.jsx
import React, { useState, useEffect } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from '../components/OptionTab';
import Sidebar from '../components/Sidebar';
import undefinedData from '../data/undefinedData.json';

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Get the category titles from the keys in undefinedData.json
    const categoryTitles = Object.keys(undefinedData);
    setCategories(categoryTitles);
  }, []);

  const handleDropInArea = (item) => {
    setDroppedItems([...droppedItems, item]);
  };

  const handleDropInContent = (itemIndex) => {
    const updatedItems = [...droppedItems];
    const item = updatedItems.splice(itemIndex, 1)[0];
    setDroppedItems(updatedItems);
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      <CssBaseline />
      <div
        style={{
          display: 'flex',
          overflowX: 'auto',
          whiteSpace: 'nowrap',
          padding: '10px',
          border: '1px solid #ccc',
        }}
      >
        <Sidebar titles={categories} onSelect={handleCategorySelect} />
      </div>

      <Container
        style={{
          minHeight: '100vh',
          border: '2px dashed #ccc',
          padding: '20px',
          position: 'relative',
        }}
      >
        <h1>Main Content</h1>

        {selectedCategory && (
          <div>
            <h2>{selectedCategory}</h2>
            <div
              style={{
                width: '200px',
                height: '200px',
                border: '2px dashed #ccc',
                marginBottom: '20px',
              }}
              onDrop={(e) => {
                e.preventDefault();
                const item = e.dataTransfer.getData('text/plain');
                handleDropInArea(item);
              }}
              onDragOver={(e) => e.preventDefault()}
            >
              {droppedItems.map((item, index) => (
                <div
                  key={index}
                  style={{
                    border: '1px solid #ccc',
                    padding: '10px',
                    marginBottom: '10px',
                    cursor: 'move',
                  }}
                  draggable
                  onDragStart={(e) => {
                    e.dataTransfer.setData('text/plain', index.toString());
                  }}
                  onDragEnd={() => handleDropInContent(index)}
                >
                  {item}
                </div>
              ))}
            </div>
          </div>
        )}
        <OptionTab items={undefinedData[selectedCategory]} />
      </Container>
    </div>
  );
};

export default Undefined;
