import React, { useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider } from '@mui/material';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const PreDefinedPath = ({ pathways }) => {
  /*
  const { id } = useParams();
  console.log(id)   // Find the selected pathway by id
  console.log(pathways[0].title)
  console.log(pathways[0].steps[0].content)
  return(
    <> 

    <h1>{pathways[0].title}</h1>
    <li>
      <ul>
        {pathways[0].steps.map(item => (
          <li key={item.id}>{item.content}</li>
        ))}
      </ul>
    </li>
    </> 

  ) */
  const { id } = useParams()

  return (
    <>
    {
    pathways[id].steps.map((item, index) => (
      <div style={{backgroundColor:'lightblue', margin:'20px 25%', textAlign:'center', fontSize:'40px'}}
        key={index}
        draggable>
          {item.content}
      </div>
      ))}
    </>
  );
};

export default PreDefinedPath;
