import React, { useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider } from '@mui/material';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const PreDefinedPath = ({ pathways }) => {
  const { id } = useParams();

  // Find the selected pathway by id
  const selectedPathway = pathways.find(pathway => pathway.id === id);
  const stepsWithIds = selectedPathway.steps.map((step, index) => ({
    id:index, // Include pathway ID
    content: step.content,
  }));

  const [steps, setSteps] = useState(stepsWithIds);

  // Check if the selected pathway is not found
  if (!selectedPathway) {
    return <div>Pathway not found</div>;
  }

  // Function to handle the drag-and-drop end event
  const onDragEnd = result => {
    if (!result.destination) {
      return;
    }

    // Reorder the steps based on the drag-and-drop result
    const newSteps = [...steps];
    const [movedStep] = newSteps.splice(result.source.index, 1);
    newSteps.splice(result.destination.index, 0, movedStep);

    // Update the state with the new order
    setSteps(newSteps);
  };

  return (
    <div>
      <h2>{selectedPathway.title} Pathway</h2>

    </div>
  );
};

export default PreDefinedPath;
