import React, { useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider } from '@mui/material';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

import Theme from '../components/Theme'; // Import your theme

const PreDefinedPath = ({ pathways }) => {
  const { id } = useParams();
  const [reorderedSteps, setReorderedSteps] = useState(pathways[id].steps);

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const newSteps = reorder(
      reorderedSteps,
      result.source.index,
      result.destination.index
    );

    setReorderedSteps(newSteps);
  };

  const reorder = (list, startIndex, endIndex) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
  };

  const grid = 8;

  const getItemStyle = (isDragging, draggableStyle) => ({
    userSelect: "none",
    padding: grid * 2,
    margin: `0 0 ${grid}px 0`,
    background: isDragging ? Theme.colors.lightGreen : Theme.colors.grey,
    ...draggableStyle
  });

  const getListStyle = isDraggingOver => ({
    background: isDraggingOver ? Theme.colors.lightBlue : Theme.colors.lightGrey,
    padding: grid,
    width: 250
  });

  if (!pathways[id]) {
    return <div>Pathway not found</div>;
  }

  return (
    <Container>
      <Typography variant="h4">{pathways[id].title} Pathway</Typography>
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="droppable">
          {(provided, snapshot) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              style={getListStyle(snapshot.isDraggingOver)}
            >
              {reorderedSteps.map((step, index) => (
                <Draggable key={index} draggableId={`step-${index}`} index={index}>
                  {(provided, snapshot) => (
                    <ListItem
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={getItemStyle(
                        snapshot.isDragging,
                        provided.draggableProps.style
                      )}
                    >
                      <ListItemText primary={`${index + 1}. ${step}`} />
                    </ListItem>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </Container>
  );
};

export default PreDefinedPath;
