import React, { useState } from 'react';

const PathStepBox = ({ step }) => {
  const [currentStep, setCurrentStep] = useState(0);
  const [expandedResource, setExpandedResource] = useState(null);

  const nextStep = () => {
    if (currentStep < (step.details || []).length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const toggleResourceAccordion = (resourceIndex) => {
    if (expandedResource === resourceIndex) {
      setExpandedResource(null);
    } else {
      setExpandedResource(resourceIndex);
    }
  };

  return (
    <div>
      <h4>{step.content}</h4>
      {step.details ||
        step.steps.map((item, index) =>
          item.details.map((deets) => (
            <div key={index}>
              <p>
                <strong>Name:</strong> {deets.title}
              </p>
              <p>
                <strong>Description:</strong> {deets.content}
              </p>
              <ul className="skills-list">
                {deets.skills &&
                  deets.skills.map((skill, skillIndex) => (
                    <li key={skillIndex} style={{ width: '90%' }}>
                      <p>{skill.title}</p>
                      <button onClick={() => toggleResourceAccordion(skillIndex[0])}>
                        Toggle Resource
                      </button>
                      {expandedResource === skillIndex && (
                        <ol>
                          <li>
                            <a href={skill.resource}>{skill.resource}</a>
                          </li>
                          {/* Additional list items can be added here */}
                        </ol>
                      )}
                    </li>
                  ))}
              </ul>
            </div>
          ))
        )}
    </div>
  );
};

export default PathStepBox;
