// OptionTab.jsx
import React, { useState } from 'react';
import { Box, Typography } from '@mui/material';

const OptionTab = ({ items }) => {
  const [list, setList] = useState(items || []);

  const dragStartHandler = (e, position) => {
    e.dataTransfer.setData('text/plain', position.toString());
  };

  const dragOverHandler = (e) => {
    e.preventDefault();
  };

  const dropHandler = (e) => {
    e.preventDefault();
    const newPosition = list.length; // Insert at the end by default

    // Get the X coordinate of the drop event relative to the OptionTab container
    const dropX = e.clientX - e.target.getBoundingClientRect().left;

    // Calculate the position based on the X coordinate
    list.forEach((item, index) => {
      const itemWidth = 170; // Adjust this value as needed
      const itemOffset = index * itemWidth;
      if (dropX >= itemOffset && dropX <= itemOffset + itemWidth) {
        newPosition = index;
      }
    });

    const copyListItems = [...list];
    const [draggedItem] = copyListItems.splice(newPosition, 1);
    copyListItems.splice(newPosition, 0, draggedItem);
    setList(copyListItems);
  };

  return (
    <div
      style={{
        display: 'flex',
        width: '100%',
        overflowX: 'auto',
        whiteSpace: 'nowrap', // Prevent items from wrapping to the next line
      }}
      onDragOver={dragOverHandler}
      onDrop={dropHandler}
    >
      {list.map((item, index) => (
        <Box
          key={item.id}
          sx={{
            minWidth: '150px',
            border: '1px solid #ccc',
            padding: '10px',
            marginRight: '10px',
            display: 'inline-block', // Display items inline
            verticalAlign: 'top', // Align items vertically at the top
          }}
          onDragStart={(e) => dragStartHandler(e, index)}
          draggable
        >
          <Typography variant="h6">{item.title}</Typography>
          <p>{item.description}</p>
        </Box>
      ))}
    </div>
  );
};

export default OptionTab;
