import React, { useState, useEffect } from 'react';
import { useParams, useLocation, Link } from 'react-router-dom';
import PathStepBox from '../components/PathStepBox';
import {
  Container,
  Typography,
  Paper,
  Button,
  Box,
} from '@mui/material';

const PathInfoPage = ({ pathways }) => {
  const { id } = useParams();
  const location = useLocation();
  const updatedList = location.state ? location.state.updatedList : null;
  const path = pathways[id];
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    if (currentStep < (updatedList || path.steps).length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  // Function to count items by category in the updatedList
  const countItemsByCategory = () => {
    const counts = {};

    if (updatedList) {
      updatedList.forEach((item) => {
        const category = item.category; // Replace with the actual property name in your data
        if (category) {
          counts[category] = (counts[category] || 0) + 1;
        }
      });
    }

    return counts;
  };

  // Function to check if a category has more than 2 items
  const categoryHasMoreThanTwo = (categoryCounts) => {
    return Object.values(categoryCounts).some((count) => count > 2);
  };

  const steps = updatedList || path.steps;
  const currentStepContent = steps[currentStep];
  const categoryCounts = countItemsByCategory();

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} sx={{ padding: 3, marginTop: 2 }}>
        <Typography variant="h4" gutterBottom>
          {updatedList[0].title ? "Bespoke Pathway" : path.title }
        </Typography>
        <Typography variant="body1" paragraph>
          {updatedList[0].title ? " " :  path.desc}
        </Typography>
        <Typography variant="body1">
          { updatedList[0].title ? "Unknown " : "Average Pay:"+ path.pay}
        </Typography>

        <Box marginTop={2}>
          <Typography variant="h5" gutterBottom>
            Step {currentStep + 1}
          </Typography>
          <PathStepBox step={currentStepContent} />
          <Box display="flex" justifyContent="space-between" marginTop={2}>
            <Button
              variant="contained"
              color="primary"
              onClick={prevStep}
              disabled={currentStep === 0}
            >
              Previous Step
            </Button>
            <Button
              variant="contained"
              color="primary"
              onClick={nextStep}
              disabled={currentStep === steps.length - 1}
            >
              Next Step
            </Button>
          </Box>
        </Box>




      </Paper>
       {/* Conditional rendering of career titles and courses */}
       {categoryHasMoreThanTwo(categoryCounts) && (
          <Box marginTop={2}>
            <Typography variant="h5" gutterBottom>
              Possible Career Titles and Courses
            </Typography>
            {/* Here, you can map and display career titles and courses for the category */}
          </Box>
        )}
    </Container>

  );
};

export default PathInfoPage;
