import React, { useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider } from '@mui/material';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const PreDefinedPath = ({ pathways }) => {
  const { id } = useParams();
  const [reorderedSteps, setReorderedSteps] = useState(pathways[id].steps);

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const newSteps = Array.from(reorderedSteps);
    const [reorderedStep] = newSteps.splice(result.source.index, 1);
    newSteps.splice(result.destination.index, 0, reorderedStep);

    setReorderedSteps(newSteps);
  };

  if (!pathways[id]) {
    return <div>Pathway not found</div>;
  }

  return (
    <Container>
      {/* ... */}
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="steps">
          {(provided) => (
            <List {...provided.droppableProps} ref={provided.innerRef}>
              {reorderedSteps.map((step, index) => (
                <Draggable key={index} draggableId={`${index}`} index={index}>
                  {(provided) => (
                    <ListItem
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                    >
                      <ListItemText primary={`${index}. ${step}`} />
                    </ListItem>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </List>
          )}
        </Droppable>
      </DragDropContext>
    </Container>
  );
};

export default PreDefinedPath;
