import React from 'react';
import { PDFViewer, Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    margin: 10,
  },
  content: {
    margin: 10,
  textAlign: 'left',
  borderBottom: 1,
    borderBottomColor: 'black',
  },
  title: {
    fontSize: 16,
  },
  subTitle: {
    fontSize: 14,
  },
  skills: {
    marginLeft: 10,
  },
  skill: {
    marginLeft: 10,
  },
});

const PathStepBox = ({ step }) => {
  const generatePDF = () => {
    const MyDocument = () => (
      <Document>
        <Page size="A4" style={styles.page}>
          <View style={styles.content}>
            <Text style={styles.title}>{step.content}</Text>
          </View>
        </Page>
        {step.steps.map((s, stepIndex) => (
          <Page key={stepIndex} size="A4" style={styles.page}>
            <View style={styles.content}>
              <Text style={styles.subTitle}>Name: {s.title}</Text>
              <Text style={styles.subTitle}>Description: {s.description}</Text>
            </View>
            <View style={styles.skills}>
              {s.skills.map((skill, skillIndex) => (
                <View key={skillIndex} style={styles.skill}>
                  <Text>Skill: {skill.title}</Text>
                  {skill.resource && <Text>Resource: {skill.resource}</Text>}
                </View>
              ))}
            </View>
          </Page>
        ))}
      </Document>
    );

    return (
      <div>
        <PDFViewer style={{ width: '100%', height: '800px' }}>
          <MyDocument />
        </PDFViewer>
      </div>
    );
  };

  return (
    <div>
      <h4>{step.content}</h4>
      {step.details ||
        step.steps.map((item, index) =>
          item.details.map((deets) => (
            <div key={index}>
              <p>
                <strong>Name:</strong> {deets.title}
              </p>
              <p>
                <strong>Description:</strong> {deets.content}
              </p>
              <ul className="skills-list">
                {deets.skills &&
                  deets.skills.map((skill, skillIndex) => (
                    <li key={skillIndex}>
                      <p>{skill.title}</p>
                      {skill.resource && (
                        <p>
                          <strong>Resource:</strong>
                          <a href={skill.resource} target="_blank" rel="noopener noreferrer">
                            {skill.resource}
                          </a>
                        </p>
                      )}
                    </li>
                  ))}
              </ul>
            </div>
          ))
        )}
      <button onClick={generatePDF}>Generate PDF</button>
    </div>
  );
};

export default PathStepBox;
