import React, { useState } from 'react';

const PathStepBox = ({ step, details }) => { // Receive a single step and its details as props
  const [currentDetail, setCurrentDetail] = useState(0);

  const nextDetail = () => {
    if (currentDetail < details.length - 1) {
      setCurrentDetail(currentDetail + 1);
    }
  };

  const prevDetail = () => {
    if (currentDetail > 0) {
      setCurrentDetail(currentDetail - 1);
    }
  };

  const currentDetailContent = details[currentDetail];

  return (
    <div>
      <h4>{currentDetailContent.title}</h4>
      <p>{currentDetailContent.content}</p>

      {currentDetailContent.skills && (
        <div>
          <h3>Skills You'll Gain:</h3>
          <ul>
            {currentDetailContent.skills.map((skill, index) => (
              <li key={index}>
                <strong>{skill.name}:</strong> {skill.description}{' '}
              </li>
            ))}
          </ul>
        </div>
      )}

      <button onClick={prevDetail} disabled={currentDetail === 0}>
        Previous Detail
      </button>
      <button onClick={nextDetail} disabled={currentDetail === details.length - 1}>
        Next Detail
      </button>
    </div>
  );
};

export default PathStepBox;
