import React, { useState } from 'react';

const PathStepBox = ({ step }) => { // Receive a single step as a prop
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    if (currentStep < step.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

