import React, { useState } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from '../components/OptionTab';
import Sidebar from '../components/Sidebar';

import undefinedData from '../data/undefinedData.json'; // Import JSON data

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]);
  const [nextItemId, setNextItemId] = useState(1); // Unique identifier for items
  const [selectedCategory, setSelectedCategory] = useState(null); // Default selected category
  const [selectedSubcategory, setSelectedSubcategory] = useState(null); // Default selected subcategory
  const [droppedItemTitles, setDroppedItemTitles] = useState([]); // Track dropped item titles

  const handleDropInArea = (item) => {
    // Find the selected category and subcategory in the data
    const categoryData = undefinedData[selectedCategory];
    if (!categoryData) {
      return;
    }

    const subcategoryData = categoryData[selectedSubcategory];
    if (!subcategoryData) {
      return;
    }

    const selectedItem = subcategoryData.find((dataItem) => dataItem.title === item);
    if (selectedItem) {
      setDroppedItems([...droppedItems, { id: nextItemId, ...selectedItem }]);
      setDroppedItemTitles([...droppedItemTitles, selectedItem.title]); // Add title to dropped list
      setNextItemId(nextItemId + 1);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      <CssBaseline />

      <Sidebar data={undefinedData} onSelectCategory={(category) => setSelectedCategory(category)} />
      {selectedCategory && selectedSubcategory && (
        <div
          style={{
            display: 'flex',
            overflowX: 'auto',
            whiteSpace: 'nowrap',
            padding: '10px',
            border: '1px solid #ccc',
          }}
        >
          <OptionTab
            items={undefinedData[selectedCategory][selectedSubcategory]}
            onDropInArea={handleDropInArea}
            droppedItemTitles={droppedItemTitles}
          />
        </div>
      )}
      <Container
        style={{
          height: '80%',
          border: '2px dashed #ccc',
          padding: '20px',
          position: 'relative',
        }}
      >
        <h1>Main Content</h1>
        <div
          style={{
            width: '100%',
            maxHeight: '100vh',
            minHeight: '200px',
            border: '2px dashed #ccc',
            display: 'grid',
            gap: '5px',
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', // Adjust the column width as needed
          }}
          onDrop={onDropHandler}
          onDragOver={(e) => e.preventDefault()}
        >
          {droppedItems.map((item) => (
            <div
              key={item.id}
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginBottom: '10px',
                cursor: 'move',
                width: '100%', // Adjust the width as needed
                height: '200px', // Adjust the height as needed
                position: 'relative', // Required for positioning the delete button
              }}
              draggable
              onDragStart={(e) => {
                // Set JSON data of the item being dragged
                e.dataTransfer.setData('application/json', JSON.stringify(item));
              }}
              onDragEnd={() => handleDropInContent(item.id, -1)}
            >
              <button
                style={{
                  position: 'absolute',
                  top: '5px',
                  right: '5px',
                  cursor: 'pointer',
                  background: 'none',
                  border: 'none',
                }}
                onClick={() => handleRemoveItem(item.id)}
              >
                X
              </button>
              <h3>{item.title}</h3>
              <p>{item.description}</p>
            </div>
          ))}
        </div>
      </Container>
    </div>
  );
};

export default Undefined;
