import React, { useState, useRef } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider,Box } from '@mui/material';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const PreDefinedPath = ({ pathways }) => {
  const { id } = useParams()
  const [list, setList] = useState(pathways[id].steps); // Initialize list state

  const dragItem = useRef();
  const dragOverItem = useRef();

  const dragStart = (e, position) => {
    dragItem.current = position;
    console.log(e.target.innerHTML);
  };

  const dragEnter = (e, position) => {
    dragOverItem.current = position;
    console.log(e.target.innerHTML);
  };

  const drop = (e) => {
    const copyListItems = [...list]; // Use the list state instead of pathways[id].steps
    const dragItemContent = copyListItems[dragItem.current];
    copyListItems.splice(dragItem.current, 1);
    copyListItems.splice(dragOverItem.current, 0, dragItemContent);
    dragItem.current = null;
    dragOverItem.current = null;
    setList(copyListItems); // Update the list state
  };

  return (
    <>

      {list.map((item, index) => (
        <Box
        sx={{
          width: 300,
          height: 300,
          backgroundColor: 'primary.dark',
          '&:hover': {
            backgroundColor: 'primary.main',
            opacity: [0.9, 0.8, 0.7],
          },
        }}
          key={item.id}

          onDragStart={(e) => dragStart(e, index)}
          onDragEnter={(e) => dragEnter(e, index)}
          onDragEnd={drop}
          draggable
        >
          {item.content}
        </Box>
      ))}
    </>

  );
};

export default PreDefinedPath;
