import React, { useState } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from './OptionTab';

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]); // State to store dropped items

  // Handle the drop action in the open squared area
  const handleDropInArea = (item) => {
    setDroppedItems([...droppedItems, item]);
  };

  // Handle the drop action in the main content area
  const handleDropInContent = (itemIndex) => {
    const updatedItems = [...droppedItems];
    const item = updatedItems.splice(itemIndex, 1)[0];
    setDroppedItems(updatedItems);
  };

  return (
    <div style={{ display: 'flex' }}>
      <CssBaseline />
      <Container
        style={{
          minHeight: '100vh',
          border: '2px dashed #ccc',
          padding: '20px',
          position: 'relative',
        }}
      >
        {/* Your main content goes here */}
        <h1>Main Content</h1>

        {/* Render items in the open squared area */}
        <div
          style={{
            width: '200px',
            height: '200px',
            border: '2px dashed #ccc',
            marginBottom: '20px',
          }}
          onDrop={(e) => {
            e.preventDefault();
            const item = e.dataTransfer.getData('text/plain');
            handleDropInArea(item);
          }}
          onDragOver={(e) => e.preventDefault()}
        >
          {/* Render dropped items in the open squared area */}
          {droppedItems.map((item, index) => (
            <div
              key={index}
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginBottom: '10px',
                cursor: 'move',
              }}
              draggable
              onDragStart={(e) => {
                e.dataTransfer.setData('text/plain', index.toString());
              }}
              onDragEnd={() => handleDropInContent(index)}
            >
              {item}
            </div>
          ))}
        </div>
      </Container>

      {/* Render the OptionTab component */}
      <OptionTab />
    </div>
  );
};

export default Undefined;
