import React, { useState, useRef } from 'react';
import { Box, Typography } from '@mui/material';

const OptionTab = ({ onDragStart, onDragEnd }) => {
  const items = ['test1', 'test2', 'test3'];
  const [list, setList] = useState(items);
  const dragItem = useRef();
  const dragOverItem = useRef();

  const dragStartHandler = (e, position) => {
    dragItem.current = position;
    onDragStart();
  };

  const dragEnterHandler = (e, position) => {
    dragOverItem.current = position;
  };

  const dropHandler = () => {
    if (dragItem.current !== dragOverItem.current) {
      const copyListItems = [...list];
      const dragItemContent = copyListItems[dragItem.current];
      copyListItems.splice(dragItem.current, 1);
      copyListItems.splice(dragOverItem.current, 0, dragItemContent);
      setList(copyListItems);
      onDragEnd();
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', overflowX: 'scroll', width: '100%' }}>
        {list.map((item, index) => (
          <Box
            key={item}
            sx={{
              minWidth: '150px',
              border: '1px solid #ccc',
              padding: '10px',
              marginRight: '10px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
            onDragStart={(e) => dragStartHandler(e, index)}
            onDragEnter={(e) => dragEnterHandler(e, index)}
            onDragEnd={dropHandler}
            draggable
          >
            <Typography variant="h6">{item}</Typography>
          </Box>
        ))}
      </div>
    </div>
  );
};

export default OptionTab;
