import React, { useState } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from './OptionTab';

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]);

  const handleDropInArea = (item) => {
    setDroppedItems([...droppedItems, item]);
  };

  const handleDropInContent = (draggedItem, dropPosition) => {
    const updatedItems = [...droppedItems];

    if (dropPosition === -1) {
      updatedItems.unshift(draggedItem); // Insert at the beginning
    } else if (dropPosition === updatedItems.length) {
      updatedItems.push(draggedItem); // Insert at the end
    } else {
      updatedItems.splice(dropPosition, 0, draggedItem); // Insert at the specified position
    }

    setDroppedItems(updatedItems);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      <CssBaseline />

      <div
        style={{
          display: 'flex',
          overflowX: 'auto',
          whiteSpace: 'nowrap',
          padding: '10px',
          border: '1px solid #ccc',
        }}
      >
        <OptionTab />
      </div>

      <Container
        style={{
          minHeight: '100vh',
          border: '2px dashed #ccc',
          padding: '20px',
          position: 'relative',
        }}
      >
        <h1>Main Content</h1>

        <div
          style={{
            width: '200px',
            height: '200px',
            border: '2px dashed #ccc',
            marginBottom: '20px',
          }}
          onDrop={(e) => {
            e.preventDefault();
            const item = e.dataTransfer.getData('text/plain');
            handleDropInArea(item);
          }}
          onDragOver={(e) => e.preventDefault()}
        >
          {droppedItems.map((item, index) => (
            <div
              key={index}
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginBottom: '10px',
                cursor: 'move',
              }}
              draggable
              onDragStart={(e) => {
                e.dataTransfer.setData('text/plain', item);
              }}
              onDragEnd={() => handleDropInContent(item, index)}
            >
              {item}
            </div>
          ))}
        </div>
      </Container>
    </div>
  );
};

export default Undefined;
