import React, { useState } from 'react';
import { Typography, Tooltip, IconButton } from '@mui/material';
import InfoIcon from '@mui/icons-material/Info';
import StepModal from './StepModal'; // Import the Modal component

const OptionTab = ({ items, onDropInArea, droppedItemTitles }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  const openModal = (item) => {
    setSelectedItem(item);
    setModalOpen(true);
  };

  const closeModal = () => {
    setSelectedItem(null);
    setModalOpen(false);
  };

  const onDragStartHandler = (e, item) => {
    // Set the item's title as text/plain during drag
    e.dataTransfer.setData('text/plain', item.title);
  };

  const onDragOverHandler = (e) => {
    e.preventDefault();
  };

  const onDropHandler = (e, item) => {
    e.preventDefault();
    const droppedItemTitle = e.dataTransfer.getData('text/plain');
    console.log('Dropped item title:', droppedItemTitle);
    onDropInArea(droppedItemTitle); // Pass the title of the dropped item
  };

  return (
    <div>
      <div
        style={{
          width: '100vw',
          overflowX: 'scroll',
          whiteSpace: 'nowrap',
        }}
        onDragOver={onDragOverHandler}
        onDrop={(e) => onDropHandler(e, items)}
      >
        {items.map((item) => (
          <Tooltip key={item.id} title={item.tooltip} placement="top">
            <div
              style={{
                width: '30%',
                border: '1px solid #ccc',
                padding: '10px',
                marginRight: '10px',
                display: 'inline-block',
                verticalAlign: 'top',
                overflow: 'hidden',
                position: 'relative',
              }}
            >
              {droppedItemTitles.includes(item.title) && (
                <div
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '50%',
                    height: '100%',
                    backgroundColor: 'rgba(128, 128, 128, 0.3)', // Grey overlay with reduced opacity
                    zIndex: 1, // Ensure the overlay doesn't interfere with dragging
                  }}
                ></div>
              )}
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Typography variant="h6">{item.title}</Typography>
                <Tooltip title="More Info">
                  <IconButton
                    size="small"
                    style={{ padding: 0 }}
                    onClick={() => openModal(item)} // Open the modal when the icon is clicked
                  >
                    <InfoIcon fontSize="small" />
                  </IconButton>
                </Tooltip>
              </div>
              <Typography variant="body1">{item.description}</Typography>
            </div>
          </Tooltip>
        ))}
      </div>

      {/* Render the Modal component */}
      {selectedItem && (
        <StepModal open={modalOpen} onClose={closeModal} item={selectedItem} />
      )}
    </div>
  );
};

export default OptionTab;
