// Undefined.js
import React, { useState } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import OptionTab from '../components/OptionTab';
import Sidebar from '../components/Sidebar';
import undefinedData from '../data/undefinedData.json';

const Undefined = () => {
  const [droppedItems, setDroppedItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('games');

  const handleDropInArea = (item) => {
    setDroppedItems([...droppedItems, item]);
  };

  const handleDropInContent = (itemIndex) => {
    const updatedItems = [...droppedItems];
    const item = updatedItems.splice(itemIndex, 1)[0];
    setDroppedItems(updatedItems);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      <CssBaseline />
      <Sidebar titles={['games', 'web']} onSelectTitle={handleCategoryChange} />
      <div
        style={{
          display: 'flex',
          overflowX: 'auto',
          whiteSpace: 'nowrap',
          padding: '10px',
          border: '1px solid #ccc',
        }}
      >
        <OptionTab items={undefinedData[selectedCategory]} />
      </div>


      <Container
        style={{
          minHeight: '100vh',
          border: '2px dashed #ccc',
          padding: '20px',
          position: 'relative',
        }}
      >
        <h1>Main Content</h1>

        <div
          style={{
            width: '200px',
            height: '200px',
            border: '2px dashed #ccc',
            marginBottom: '20px',
          }}
          onDrop={(e) => {
            e.preventDefault();
            const item = e.dataTransfer.getData('text/plain');
            handleDropInArea(item);
          }}
          onDragOver={(e) => e.preventDefault()}
        >
          {droppedItems.map((item, index) => (
            <div
              key={index}
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginBottom: '10px',
                cursor: 'move',
              }}
              draggable
              onDragStart={(e) => {
                e.dataTransfer.setData('text/plain', index.toString());
              }}
              onDragEnd={() => handleDropInContent(index)}
            >
              {item}
            </div>
          ))}
        </div>
      </Container>
    </div>
  );
};

export default Undefined;
