import React from 'react';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const PreDefinedPath = ({ pathways }) => {
  const { id } = useParams();
  console.log(id);
  // Find the selected pathway by id
  const selectedPathway = pathways.find(pathway => pathway.id === id);

  if (!selectedPathway) {
    return <div>Pathway not found</div>;
  }

  return (
    <div>
      <h2>{selectedPathway.title} Pathway</h2>
      <DragDropContext>
        <Droppable droppableId="step"> {/* This should match your droppableId */}
          {(provided) => (
            <ul className="steps" {...provided.droppableProps} ref={provided.innerRef}>
              {selectedPathway.steps.map((step, index) => (
                <Draggable key={step} draggableId={step} index={index}>
                  {(provided) => (
                    <li ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                      <p>{step}</p>
                    </li>
                  )}
                </Draggable>
              ))}
            </ul>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default PreDefinedPath;
