import React, { useState, useRef } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider, Box, Accordion, AccordionSummary, AccordionDetails, Tabs, Tab } from '@mui/material';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

const PreDefinedPath = ({ pathways }) => {
  const { id } = useParams();
  const [list, setList] = useState(pathways[id].steps); // Initialize list state
  const [selectedItem, setSelectedItem] = useState(null); // Track the selected item

  const dragItem = useRef();
  const dragOverItem = useRef();

  const dragStart = (e, position) => {
    dragItem.current = position;
    console.log(e.target.innerHTML);
  };

  const dragEnter = (e, position) => {
    dragOverItem.current = position;
    console.log(e.target.innerHTML);
  };

  const drop = (e) => {
    const copyListItems = [...list]; // Use the list state instead of pathways[id].steps
    const dragItemContent = copyListItems[dragItem.current];
    copyListItems.splice(dragItem.current, 1);
    copyListItems.splice(dragOverItem.current, 0, dragItemContent);
    dragItem.current = null;
    dragOverItem.current = null;
    setList(copyListItems); // Update the list state
  };

  const toggleDetails = (itemIndex) => {
    // Toggle the selected item between open and closed
    setSelectedItem((prevItem) => (prevItem === itemIndex ? null : itemIndex));
  };

  return (
    <>
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1 }}>
          {list.map((item, index) => (
            <Box
              sx={{
                backgroundColor: 'white', // Set the default background color to white
                boxShadow: '0px 0px 5px rgba(0, 0, 0, 0.3)', // Add a box shadow
                transition: 'background-color 0.3s', // Add a smooth transition for background color
                '&:hover': {
                  backgroundColor: '#FFCCB6', // Change background color on hover
                },
                margin: '20px',
                cursor: 'pointer', // Add cursor pointer to indicate items are clickable
                border: selectedItem === index ? '2px solid red' : '2px solid transparent', // Highlight selected item
                display: 'flex', // Use flex layout for the container
                flexDirection: 'column', // Stack items vertically
                alignItems: 'center', // Center items horizontally
                padding: '10px', // Add padding for spacing
                textAlign: 'center', // Center text
              }}
              key={item.id}
              onDragStart={(e) => dragStart(e, index)}
              onDragEnter={(e) => dragEnter(e, index)}
              onDragEnd={drop}
              draggable
              onClick={() => toggleDetails(index)} // Handle click to toggle details
            >
              <h1 style={{ marginBottom: '10px' }}>{index + 1}</h1> {/* Add margin to separate h1 from content */}
              {item.content}
              <Accordion
                expanded={selectedItem === index}
                style={{ marginTop: '10px' }}
                onChange={() => toggleDetails(index)}
              >
                <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                  <Typography variant="h6">Skills You'll Gain</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <ul>
                    {item.details &&
                      item.details[0].skills.map((skill, skillIndex) => (
                        <li key={skillIndex}>{skill}</li>
                      ))}
                  </ul>
                </AccordionDetails>
              </Accordion>
            </Box>
          ))}
        </div>
      </div>
    </>
  );
};

export default PreDefinedPath;
