import React, { useState } from 'react';

const Sidebar = ({ data, onSelectCategory }) => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    onSelectCategory(category); // Call the callback function with the selected category
    if(category.Contains("_")){
      category =  category.replace("_", " ")
     }
  };

  return (
    <div style={{ width: '250px', position: 'fixed', top: '200px', left: 0, height: '100%', background: '#fff' }}>
      <h2>Categories</h2>
      <ul>
        {Object.keys(data).map((category) => (
          <li
            key={category}
            onClick={() => handleCategoryClick(category)}
            style={{ cursor: 'pointer', color: selectedCategory === category ? 'blue' : 'black' }}
          >

            {category}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
