import React, { useState } from 'react';
import { Accordion, AccordionSummary, AccordionDetails, Typography } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import jsPDF from 'jspdf';

const PathStepBox = ({ step }) => {
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    if (currentStep < (step.details || []).length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  // Function to generate and download the PDF
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text(step.content, 10, 10);
    step.details.forEach((deets) => {
      doc.text(`Name: ${deets.title}`, 10, doc.autoTable.previous.finalY + 10);
      doc.text(`Description: ${deets.content}`, 10, doc.autoTable.previous.finalY + 10);
      deets.skills.forEach((skill) => {
        if (skill.resource) {
          doc.text(`Resource: Getting started with ${skill.title}`, 10, doc.autoTable.previous.finalY + 10);
          doc.text(skill.resource, 10, doc.autoTable.previous.finalY + 10);
        }
      });
      doc.addPage();
    });
    doc.save('path.pdf');
  };

  return (
    <div>
      <h4>{step.content}</h4>
      <button onClick={generatePDF}>Download PDF</button>
      {step.details ||
        step.steps.map((item, index) =>
          item.details.map((deets, deetsIndex) => (
            <div key={deetsIndex}>
              <p>
                <strong>Name:</strong> {deets.title}
              </p>
              <p>
                <strong>Description:</strong> {deets.content}
              </p>
              <Accordion>
                <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                  <Typography variant="h6">Resources</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <ul className="skills-list">
                    {deets.skills &&
                      deets.skills.map((skill, skillIndex) => (
                        <li key={skillIndex}>
                          <strong>{skill.title}</strong>: {skill.resource && (
                            <a href={skill.resource} target="_blank" rel="noopener noreferrer">
                              {"Getting started with: " + skill.title}
                            </a>
                          )}
                        </li>
                      ))}
                  </ul>
                </AccordionDetails>
              </Accordion>
            </div>
          ))
        )}
    </div>
  );
};

export default PathStepBox;
