import React, { useState, useRef } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider } from '@mui/material';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const PreDefinedPath = ({ pathways }) => {
  /*
  const { id } = useParams();
  console.log(id)   // Find the selected pathway by id
  console.log(pathways[0].title)
  console.log(pathways[0].steps[0].content)
  return(
    <> 

    <h1>{pathways[0].title}</h1>
    <li>
      <ul>
        {pathways[0].steps.map(item => (
          <li key={item.id}>{item.content}</li>
        ))}
      </ul>
    </li>
    </> 

  ) */
  const { id } = useParams()
  const dragItem = useRef();
  const dragOverItem = useRef();
  const [list, setList] = useState(pathways[id].steps)
  const dragStart = (e, position) => {
    dragItem.current = position;
    console.log(e.target.innerHTML);
  };

  const dragEnter = (e, position) => {
    dragOverItem.current = position;
    console.log(e.target.innerHTML);
  };

  const drop = (e) => {
    const copyListItems = [...pathways[id].steps];
    const dragItemContent = copyListItems[dragItem.current];
    copyListItems.splice(dragItem.current, 1);
    copyListItems.splice(dragOverItem.current, 0, dragItemContent);
    dragItem.current = null;
    dragOverItem.current = null;
    setList(copyListItems);
  };
  return (
    <>
    {
    pathways[id].steps&&
    pathways[id].steps.map((item, index) => (

      <div style={{backgroundColor:'lightblue', margin:'20px 25%', textAlign:'center', fontSize:'40px'}}
      onDragStart={(e) => dragStart(e, index)}
      onDragEnter={(e) => dragEnter(e, index)}
      onDragEnd={drop}
        draggable>
          {item.content}
          {index}
      </div>
      ))}
    </>
  );
};

export default PreDefinedPath;
