import React, { useState } from 'react';

const PathStepBox = ({ step }) => { // Receive a single step as a prop
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    if (currentStep < step.skills.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  // Get the current step using currentStep
  const currentSkill = step.skills[currentStep];

  return (
    <div>
      <h4>{step.content}</h4>

      {currentSkill && (
        <div>
          <p><strong>Name:</strong> {currentSkill.name}</p>
          <p><strong>Description:</strong> {currentSkill.description}</p>
          <p style={{ color: 'red' }}>{currentSkill.type} skill</p>
        </div>
      )}

      {step.resources && step.resources.length > 0 && (
        <div>
          <h3>Learning Resources:</h3>
          <ul>
            {step.resources.map((resource, index) => (
              <li key={index}>
                <a href={resource.link} target="_blank" rel="noopener noreferrer">
                  {resource.title}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default PathStepBox;
