import React, { useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Divider } from '@mui/material';
import { useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const PreDefinedPath = ({ pathways }) => {
  const { id } = useParams();
  console.log(id)
  // Find the selected pathway by id
  const selectedPathway = pathways.find(pathway => pathway.id === id);

  if (!selectedPathway) {
    return <div>Pathway not found</div>;
  }

  return (
    <div>
      <h2>{selectedPathway.title} Pathway</h2>
        <DragDropContext>
          <Droppable droppableId={"step"}>
            {(provided) => (
              <ul className="step" {...provided.droppableProps} ref={provided.innerRef}>
                {selectedPathway.steps.map((step,id, index) => (
                   <Draggable draggableId ={id} index={index} type="TASK">

                   {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <h4>{step}</h4>
                      </div>
                  )}
          </Draggable>
                ))}
              </ul>
              )}
            </Droppable>
        </DragDropContext>
    </div>

  );
};


export default PreDefinedPath;
