import React, { useState, useRef } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Sidebar from './Sidebar'; // Import your Sidebar component here

const Undefined = () => {
  const items = ['test1', 'test2', 'test3'];
  const [list, setList] = useState(items);

  const dragItem = useRef();

  const dragStartHandler = (e, position) => {
    dragItem.current = position;
    e.dataTransfer.setData('text/plain', position.toString());
  };

  const dragOverHandler = (e) => {
    e.preventDefault();
  };

  const dropHandler = (e) => {
    e.preventDefault();
    const newPosition = list.length;

    // Calculate the position based on the X coordinate
    // ...

    const copyListItems = [...list];
    const [draggedItem] = copyListItems.splice(dragItem.current, 1);
    copyListItems.splice(newPosition, 0, draggedItem);
    setList([...copyListItems]);
    dragItem.current = null;
  };

  return (
    <div style={{ display: 'flex' }}>
      <CssBaseline />
      <Sidebar items={list} onDragStart={dragStartHandler} />
      <Container
        onDragOver={dragOverHandler}
        onDrop={dropHandler}
        style={{
          minHeight: '100vh',
          border: '2px dashed #ccc',
          padding: '20px',
          position: 'relative',
        }}
      >
        {/* Your main content goes here */}
        <h1>Main Content</h1>

        {/* Render dropped items within the droppable area */}
        {list.map((item, index) => (
          <div
            key={index}
            style={{
              border: '1px solid #ccc',
              padding: '10px',
              marginBottom: '10px',
            }}
          >
            {item}
          </div>
        ))}
      </Container>
    </div>
  );
};

export default Undefined;
