import React from 'react';
import { Typography } from '@mui/material';

const OptionTab = ({ items, onDropInArea, droppedItemTitles }) => {
  const onDragStartHandler = (e, item) => {
    // Set the item's title as text/plain during drag
    e.dataTransfer.setData('text/plain', item.title);
  };

  const onDragOverHandler = (e) => {
    e.preventDefault();
  };

  const onDropHandler = (e, item) => {
    e.preventDefault();
    const droppedItemTitle = e.dataTransfer.getData('text/plain');
    console.log('Dropped item title:', droppedItemTitle);
    onDropInArea(droppedItemTitle); // Pass the title of the dropped item
  };

  return (
    <div
      style={{
        display: 'flex',
        width: '100%',
        overflowX: 'scroll',
        whiteSpace: 'nowrap',
      }}
      onDragOver={onDragOverHandler}
      onDrop={(e) => onDropHandler(e, items)}
    >
      {items.map((item) => (
        <div
          key={item.id}
          style={{
            width:'100%',
            border: '1px solid #ccc',
            padding: '10px',
            marginRight: '10px',
            display: 'inline-block',
            verticalAlign: 'top',
            overflow:'hidden',
            position: 'relative', // Required for the overlay
          }}
          onDragStart={(e) => onDragStartHandler(e, item)}
          draggable={!droppedItemTitles.includes(item.title)} // Disable dragging if in dropped list
        >
          {droppedItemTitles.includes(item.title) && (
            <div
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                backgroundColor: 'rgba(128, 128, 128, 0.3)', // Grey overlay with reduced opacity
                zIndex: 1, // Ensure the overlay doesn't interfere with dragging
              }}
            ></div>
          )}
          <Typography variant="h6">{item.title}</Typography>
          <Typography variant="body1">{item.description}</Typography>
        </div>
      ))}
    </div>
  );
};

export default OptionTab;
